package java_data_types;

public class Day08_while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=0;
		System.out.println("the value of a before the while loop is "+a);
		while (a<5) {
			System.out.println("hello");
			a++;}
			System.out.println("the vlaue of a after the while loop is "+a);
		
	}

}
